	@PostMapping(value = "sendMail3")
	public ModelAndView sendMail3(HttpServletRequest request) throws MessagingException {
		ModelAndView mav = new ModelAndView("content");
		String to = request.getParameter("to");
		String subject = request.getParameter("subject");
		String htmlText2 = request.getParameter("text");
		System.out.println("To: " + to + " subject: " + subject + " text: " + htmlText2);
		mav.addObject("to", to);
		mav.addObject("subject", subject);
		mav.addObject("htmlText2", htmlText2);
		return mav;
	}

	@PostMapping(value = "sendMail")
	public ModelAndView sendMail(HttpServletRequest request) throws MessagingException {
		logger.info("Send Mail called");
		ModelAndView mav = new ModelAndView("message");
		String to = request.getParameter("to");
		String subject = request.getParameter("subject");
//		String htmlText2 = request.getParameter("text");
//		String htmlText2 = "<img src='data:image/png;base64,'>";
		String htmlText2 = "<img src='data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg== 'alt='Red dot' />";
		System.out.println("To: " + to + " subject: " + subject + " text: " + htmlText2);
//		String htmlText2="<p><img src="data:image/jpeg;base64," data-filename="small-icon-images-11.jpg" style="width: 257px;"><br></p>";
		MimeMessage message = mailSender.createMimeMessage();
		MimeMessageHelper helper = new MimeMessageHelper(message, true);
		helper.setFrom("GulrezFarooqui");
		helper.setTo(to);
		helper.setSubject(subject);

		helper.setText(htmlText2, true);

		mailSender.send(message);
		System.out.println("Mail Sent Successfully");
		return mav;
	}

